LibStub('LibSimpleOptions-1.0').AddOptionsPanel('pStats', function(self)	local db = pStatsDB or {colors = {0, 1, 1}, sorted = true}	local title, sub = self:MakeTitleTextAndSubText('pStats', 'These options allow you to customize the looks of pStats.')	pStatsDB = db		self:MakeButton(		'name', 'Class Color',		'description', 'Set text color based on player class',		'func', function()			local l, class = UnitClass('player')			local c = RAID_CLASS_COLORS[class]			pStatsDB.colors[1] = c.r			pStatsDB.colors[2] = c.g			pStatsDB.colors[3] = c.b			self:Refresh()		end	):SetPoint('TOPLEFT', sub, 'BOTTOMLEFT', 0, -16)	self:MakeColorPicker(		'name', 'Custom Color',		'description', 'Set custom text colors with a palette',		'hasAlpha', false,		'defaultR', 0, 'defaultG', 1, 'defaultB', 1,		'getFunc', function() return unpack(db.colors) end,		'setFunc', function(r, g, b)			pStatsDB.colors[1] = r			pStatsDB.colors[2] = g			pStatsDB.colors[3] = b		end	):SetPoint('TOPLEFT', sub, 'BOTTOMLEFT', 0, -46)	self:MakeToggle(		'name', 'Toggle sorting method',		'description', 'Check to sort by memory.\nUn-check to sort by name',		'default', true,		'current', db.sorted,		'setFunc', function(value)			pStatsDB.sorted = value		end	):SetPoint('TOPLEFT', sub, 'BOTTOMLEFT', 0, -76)end)